const handlebars=require("handlebars");handlebars.registerHelper("splitArray",(r,e)=>{if(!Array.isArray(r)||"number"!=typeof e||e<1)return[];const n=r.length,t=Math.floor(n/e),a=n%e,l=Array.from({length:e},(r,e)=>e<a?t+1:t),s=[];let i=0;for(let e of l)s.push(r.slice(i,i+e)),i+=e;return s}),handlebars.registerHelper("newlineToBr",function(r){return"string"!=typeof r?r:new handlebars.SafeString(r.replace(/\n/g,"<br>"))}),handlebars.registerHelper("upperText",function(r){return"string"!=typeof r?r:new handlebars.SafeString(r.toUpperCase())});